import axios from 'axios'
import { useNavigate } from "react-router-dom";
import { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {useState} from 'react';

import {fetchAllProducts } from "../../features/Product/ProductSlice";
import Product from './Product';
import Alert from '@mui/material/Alert';
import CircularProgress from '@mui/material/CircularProgress';
import ImageListItem from '@mui/material/ImageListItem';
import Box from '@mui/material/Box';


const  ProductList=()=> {
let arrFromRedux = useSelector(s=>s.products.arrProducts)
const [filter1, setFilter1] = useState(useSelector(s => s.products.arrProducts) || []);
const statusFetchData = useSelector(s => s.products.status)
const [name, setname] = useState();


    useEffect(() => {

      fetchAllProductsList()
    }, [])

    const fetchAllProductsList = async () => {
      if(statusFetchData=="idle")
           dispatch(fetchAllProducts())
    }

    const dispatch = useDispatch()
    let nav=useNavigate();

    return  (
        <div>
        <input type="text" onChange={(e)=>name=e.target.value} placeholder='name'></input>
        <button  onClick={() =>{setFilter1 (arrFromRedux.slice().filter(i => i.name.includes( name),setname("")))}}>סינון לפי שם</button>
         <br></br>
          <button onClick={()=>{setFilter1(arrFromRedux.slice().sort((a,b)=>a.price-b.price))}}>סינון לפי מחיר</button>
          <br></br>
          <button onClick={() =>{setFilter1 (arrFromRedux.slice().filter(i => i.name.includes( "")))}}>ביטול סינונים </button>      

        <ul>
          
        {filter1 &&<Box
sx={{
    display: 'grid',
    columnGap: 3,
    rowGap: 1,
    gridTemplateColumns: 'repeat(3, 1fr)',
    marginRight:4
  }}
>
          {filter1.map((item) => (
            <ImageListItem key={item.imgUrl}>
              <Product  myProduct={item}></Product>
            </ImageListItem>
            ))}
          </Box>
         } 
        </ul>
        </div>
        
    )
}


export default ProductList;
