import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { useDispatch, useSelector } from 'react-redux';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { postOrder } from '../../features/Order/OrderSlice';
import { useNavigate } from 'react-router-dom';
import CheckIcon from '@mui/icons-material/Check';
import Swal from 'sweetalert2';
import { useLocation } from 'react-router-dom';

const defaultTheme = createTheme();

export default function FinishOrder() {
    const location = useLocation();
    const totalAmount = location.state?.totalAmount || 0;
    const dis = useDispatch();
    const nav = useNavigate();
    const userInfo = useSelector(u => u.users.currentUser);
    const cartArr = useSelector(u => u.orders.cart);
    const allUsers = useSelector(u => u.users.arrUsers);

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        let i = allUsers.findIndex(u => u.password === userInfo.password && u.name === userInfo.name);
        const newOrder = {
            userTz: allUsers[i].tz,
            userNAme: data.get('name'),
            address: data.get('address'),
            totalAmount: totalAmount,
            cart: cartArr
        }
        dis(postOrderer(newOrder));

        Swal.fire({
            position: "center",
            icon: "success",
            title: "Your order has been saved",
            showConfirmButton: false,
            timer: 1500
        });
        nav('/ViewOrderDetails')
    }

    return (
        <ThemeProvider theme={defaultTheme}>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Avatar sx={{ m: 1, bgcolor: '#00FF66' }}>
                        <CheckIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Order Confirmation                    </Typography>
                    <Typography component="h2" variant="h6">
                        totalAmount: ₪{totalAmount}
                    </Typography>
                    <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                        <Grid container spacing={2}>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="name"
                                    name="name"
                                    required
                                    fullWidth
                                    id="name"
                                    label="name"
                                    autoFocus
                                    value={userInfo.name}
                                />
                            </Grid>

                            <Grid item xs={12} sm={6}>
                                <TextField
                                    required
                                    fullWidth
                                    id="address"
                                    label="address"
                                    name="address"
                                    autoComplete="address"
                                />
                            </Grid>

                        </Grid>
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2, bgcolor: '#00FF66' }}
                        >
                            confirm order
                        </Button>
                    </Box>
                </Box>
            </Container>
        </ThemeProvider>
    );
}
