import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchAllProducts } from "../../features/Product/ProductSlice";
import Product from './Product';
import Alert from '@mui/material/Alert';
import CircularProgress from '@mui/material/CircularProgress';
import ImageListItem from '@mui/material/ImageListItem';
import Box from '@mui/material/Box';

const ProductList = () => {
  const myUser = useSelector(s => s.users.status);
  let bool = 0;
  
  const arrFromRedux = useSelector(s => s.products.arrProducts);
  const [filter1, setFilter1] = useState([]);
  const statusFetchData = useSelector(s => s.products.status);
  if (myUser === "admin")
    bool = 1;
  let name = "";
  const dispatch = useDispatch();
  let nav = useNavigate();

  useEffect(() => {
    dispatch(fetchAllProducts());
  }, [dispatch]);

  useEffect(() => {
    setFilter1(arrFromRedux);
  }, [arrFromRedux]);
  // {bool===1 return}

  return (
    <div>
      <input className='nn' type="text" onChange={(e) => name = e.target.value} placeholder='name'></input>
      <button onClick={() => { setFilter1(arrFromRedux.filter(i => i.name.includes(name))) }}>סינון לפי שם</button>
      <br></br>
      <button onClick={() => { setFilter1(arrFromRedux.slice().sort((a, b) => a.price - b.price)) }}>סינון לפי מחיר</button>
      <br></br>
      <button onClick={() => { setFilter1(arrFromRedux) }}>ביטול סינונים </button>



      <ul>
        {filter1 &&
          <Box sx={{ display: 'grid', columnGap: 3, rowGap: 1, gridTemplateColumns: 'repeat(3, 1fr)', marginRight: 4 }}>
            {filter1.map((item) => (
              <ImageListItem key={item.id}>
                <Product myProduct={item} />
              </ImageListItem>
            ))}
          </Box>}
      </ul>
    </div>
  );
}

export default ProductList;
