import { createSlice, nanoid ,createAsyncThunk } from '@reduxjs/toolkit'
import { getProducts,getProductByIdA,postProductA,putProductA, deleteProductA } from './ProductApi'

const initialState = {
    arrProducts: [],
    currentproduct:null,
    status: "idle",
    poststatus: "idle"
}

export const fetchAllProducts = createAsyncThunk(
    'product/fetchAllProducts',
    async (thunkAPI) => {
        const res = await getProducts()
        return res
    },
)

export const getProductById = createAsyncThunk(
    'product/getProductById',
    async (id,thunkAPI) => {
        const res = await getProductByIdA(id)
        return res
    },
)

export const postProduct = createAsyncThunk(
    'product/postProduct',
    async (dataProduct) => {
        const res = await postProductA(dataProduct);
        return res;
    },
)

export const putProduct = createAsyncThunk(
    'product/putProduct',
    async (product, thunkAPI) => {
        const res = await axios.put(`http://localhost:4000/product/${product.id}`, product);
        return res.data;
    },
)

export const deleteProduct = createAsyncThunk(
    'product/deleteProduct',
    async (id,thunkAPI) => {
        const res = await axios.delete(`http://localhost:4000/product/${id}`);
        return id
    },
)


export const ProductSlice = createSlice({
    name: 'product',
    initialState,
    reducers: {
        // setAllProducts: (state, action) => {
        //     state.arrProduct = action.payload
        // },
        // updateProduct: (state, action) => {
        //     let index = state.arrProduct.findIndex(x => x.id === action.payload.id)
        //     state.arrProduct.splice(index, 1, action.payload)
        // },
    },
    extraReducers: (builder) => {
        builder
            .addCase(fetchAllProducts.fulfilled, (state, { payload }) => {
                state.arrProducts = payload
                state.status = "fullfilled"
            })
            // You can chain calls, or have separate `builder.addCase()` lines each time
            .addCase(fetchAllProducts.pending, (state, action) => {
                state.status = "pending"
            })
            .addCase(fetchAllProducts.rejected, (state, action) => {
                state.status = "rejected"
            })
            .addCase(deleteProduct.fulfilled, (state, action) => {
                state.arrProducts = state.arrProducts.filter(x => x.id !== action.payload);
            })

            .addCase(putProduct.fulfilled, (state, action) => {
                let index = state.arrProducts.findIndex(x => x.id === action.payload.id);
                if (index !== -1) {
                    state.arrProducts[index] = action.payload;
                }
            });

            // .addCase(postProduct.rejected, (state, action) => {
            //     state.poststatus = "rejected"
            //     console.log(state.poststatus)
            // })

    },
})


// export const {  updateProduct } = ProductSlice.actions

export default ProductSlice.reducer
