import {useDispatch, useSelector } from "react-redux";
import * as React from 'react';
import { useLocation } from "react-router-dom";

import ImageListItemBar from '@mui/material/ImageListItemBar';
import * as MuiMaterial from '@mui/material/';
import InfoIcon from '@mui/icons-material/Info';
import { BorderBottom, CardTravel, KebabDining } from "@mui/icons-material";
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import IconButton from '@mui/material/IconButton';
import {useState} from 'react';
import { useNavigate } from "react-router-dom";
import {addToShoppingCart} from '../../features/Order/OrderSlice'
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
export default function Product(props) {
  const [quantity, setQuantity] = useState(1);
    const dis = useDispatch()
    const [showCart, setShowCart] = useState(false)
    const { state } = useLocation();
    // const { p } = state;
    const addToCart = () => {
        // dis(addToShoppingCart({ p, quantity }))
        setShowCart(true)
        setTimeout(() => {
            setShowCart(false)
        }, 5000)
    }
  const userStatus = useSelector(store => store.users.status);
    let product = props.myProduct
    const [bool, setBool] = useState(1);
    let nav=useNavigate()
  const move=()=>{
    nav('/ProductToAdd');
  }
  const renderButtons = () => {
    switch (userStatus) {
        case 'guest','customer':
            return (
              <>
              <img 
           sx={{ border:'1px solid lightpink' }}
                srcSet={`${product.imgUrl}?w=248&fit=crop&auto=format&dpr=2 2x`}
                src={`${product.imgUrl}?w=248&fit=crop&auto=format`}
                alt={product.name}
                loading="eager"
                width="100%"/>             

              <ImageListItemBar  
           sx={{
            border: '2px solid pink', // Add a pink border
            borderTop:0
          }} 
                  position="below"
                title={product.name}
                subtitle={"₪"+product.price}
                actionIcon={
                  <IconButton 
                    sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                    aria-label={`cart `}
                  ><IconButton><AddShoppingCartIcon></AddShoppingCartIcon></IconButton>
                  <IconButton><RemoveShoppingCartIcon></RemoveShoppingCartIcon></IconButton>
                  <div className="quantity-container">
                    <div className="quantity-controls">
                        <button className="quantity-button" onClick={() => setQuantity(prevQuantity => prevQuantity - 1)} disabled={quantity === 1}>
                            <span>-</span>
                        </button>
                        <span>{quantity}</span>
                        <button className="quantity-button" onClick={() => setQuantity(prevQuantity => prevQuantity + 1)}>
                            <span>+</span>
                        </button>
                    </div>
                    <button className="add-to-cart-button" onClick={addToCart}>
                        Add to Cart
                    </button>
                </div>
                    {/* {bool === 1 && (
                      <IconButton onClick={
                        () => setBool(0)
                        } >
                        <AddShoppingCartIcon />
                      </IconButton>
                    )} */}
                    {/* {bool === 0 && (
                      <IconButton onClick={move}>
                        <RemoveShoppingCartIcon />
                      </IconButton>
                    )} */}

                  </IconButton>}
                />

              </>
            );

        default:
            return null;
    }
};
