import { createSlice, nanoid ,createAsyncThunk } from '@reduxjs/toolkit'
import { getUsers,getUserByIdA,postUserA,loginA } from './UserApi'

const initialState = {
    type:"guest",
    currentUser:null,
    arrUsers:[],
    status: "idle",
    poststatus: "idle",
}

export const fetchAllUsers = createAsyncThunk(
    'user/fetchAllUsers',
    async (thunkAPI) => {
        const res = await getUsers()
        return res
    },
)

export const getUserById = createAsyncThunk(
    'user/getUserById',
    async (id,thunkAPI) => {
        const res = await getUserByIdA(id)
        return res
    },
)

export const postUser = createAsyncThunk(
    'user/postUser',
    async (newUser,thunkAPI) => {
        const res = await postUserA(newUser)
        return res
    },
)

export const login = createAsyncThunk(
    'users/login',
    async (user,thunkAPI) => {
        const res = loginA(user)
        return res
    },
)



export const UserSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        logout: (state, action) => {
            state.currentUser = action.payload
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(login.fulfilled, (state, { payload }) => {
                state.currentUser=payload
                if(state.currentUser.password==="325770659"){
                    if(state.currentUser.tz==="325770659")
                         state.type="pending"}
                  else
                  {
                    if(state.currentUser!=null)
                    // let userc=state.arrUser.filter(x=>x.username==state.currentUser.username&&x.password==state.currentUser.password)
                    // if(userc!=null)
                      state.type="fulfilled"//user
                    else
                      state.type="rejected"
                  }
                // state.status="fulfilled"
     } )
            .addCase(postUser.fulfilled, (state, { payload }) => {
                state.currentUser = payload
                state.status = "fullfilled"
                console.log(state.currentUser)
            })
            // // You can chain calls, or have separate `builder.addCase()` lines each time
            // .addCase(fetchAllProduct.pending, (state, action) => {
            //     state.status = "pending"
            // })
            // .addCase(fetchAllProduct.rejected, (state, action) => {
            //     state.status = "rejected"
            // })

    },
})


export const {  logout ,setCurrentUser} = UserSlice.actions

export default UserSlice.reducer
