import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';
import {deleteProduct} from '../../features/Product/ProductSlice'
import { addToShoppingCart } from '../../features/Order/OrderSlice';

const confirmDelete = () => {
    return new Promise((resolve) => {
        const confirmed = window.confirm("האם אתה בטוח שברצונך למחוק מוצר זה לצמיתות?");
        resolve(confirmed);
    });
};

export default function Product(props) {
    let p = props.myProduct;
    console.log(p)
    const userStatus = useSelector(store => store.users.status);
    const [quantity, setQuantity] = useState(1);
    const [showCart, setShowCart] = useState(false);
    const [bool, setBool] = useState(1);
    const dispatch = useDispatch();
    let nav = useNavigate();

    const addToCart = () => {
        setBool(1);
        dispatch(addToShoppingCart({ p, quantity }));
        setShowCart(true);
        setTimeout(() => {
            setShowCart(false);
        }, 5000);
    };

    useEffect(() => {
        if (showCart) {
            const timer = setTimeout(() => {
                setShowCart(false);
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [showCart]);

    const handleMoreDetails = () => {
        nav('/productToAdd', { state: { p } });
    };
    const handleDel =async (id) => {
        const confirmed = await confirmDelete();
        if (confirmed) {
        // פונקציה לעריכת המוצר
        dispatch(deleteProduct(id))      
               setAlertMessage('המוצר נמחק בהצלחה!');
        setTimeout(() => setAlertMessage(''), 3000);
    }   };
    const handleEdit = () => {
        // פונקציה לעריכת המוצר
        nav('/UpdateProduct/', { state: { p } })    };

    return (
        <>
            <Card sx={{ maxWidth: 300, m: 3 }}>
                <CardHeader title={p.description} />
                {<CardMedia component="img" height="140" image={p.imgUrl} />}
                <CardContent>
                    {userStatus === 'admin' ? (
                        <div>
                        <EditIcon onClick={handleEdit}/>,
                        <DeleteForeverIcon onClick={()=>{handleDel(p.id)}}/>
                        </div>
                    ) : (
                        <button onClick={handleMoreDetails}>More Details</button>
                    )}
                </CardContent>
            </Card>
            <ImageListItemBar
                sx={{
                    border: '2px solid pink', // Add a pink border
                    borderTop: 0
                }}
                position="below"
                title={p.name}
                subtitle={"₪" + p.price}
              
            />
        </>
    );
}
