import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useDispatch } from "react-redux";
import { useState } from "react";
import { postProduct } from "../../features/Product/ProductSlice";

const defaultTheme = createTheme();

export default function AddProduct() {
    let dis= useDispatch()
    const handleSubmit = (event) => {
      event.preventDefault();
      const data = new FormData(event.currentTarget);
      dis(postProduct({name:data.get('name'),description:data.get('description'),imgUrl:data.get('imgUrl'),price:data.get('price')}))
  return (<div>vyiv
<ThemeProvider theme={defaultTheme}>
      <Grid container component="main" sx={{ height: '50vh' , width:'60vw',marginLeft:'20vw'}}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://www.laline.com/media/catalog/product/cache/1/small_image/480x385/9df78eab33525d08d6e5fb8d27136e95/m/0/m08161999999.jpg)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Add Product
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="name"
                label="name"
                name="name"
                autoComplete="name"
                autoFocus
              />
              <TextField
                margin="normal"
                required
                fullWidth
                id="description"
                label="description"
                name="description"
                autoComplete="description"
                autoFocus
              />
              <TextField
                margin="normal"
                type='number'
                required
                fullWidth
                id="price"
                label="price"
                name="price"
                autoComplete="price"
                autoFocus
              />
              <TextField
                margin="normal"
                required
                fullWidth
                id="imgUrl"
                label="imgUrl"
                name="imgUrl"
                autoComplete="imgUrl"
                autoFocus
              />   
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Add
              </Button>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider></div>
  );
}}
