import { useForm } from "react-hook-form";
import TextField from '@mui/material/TextField';
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from "react-router-dom";
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Password } from '@mui/icons-material';
import { postUser} from '../../features//User/UserSlice';

const SignIn = () => {
    const [user, setUser] = useState({ tz:" ",name:" ", password:" " ,telephone:" ",address:""});
    const arrU = useSelector(x => x.users.arrUser);
    const currentUser = useSelector(u => u.users.currentUser);
    const dispatch = useDispatch();
    const nav=useNavigate()
const defaultTheme = createTheme();

    const {
        register,
        handleSubmit,
        getValues,
        formState: { errors, dirtyFields, isValid },
    } = useForm({ mode: 'onBlur' });

    const onSubmit = (data) => {

        dispatch(postUser({tz:data.tz, name:data.name, password:data.password, telephone:data.telephone,address:data.address}))
        console.log("add user")
        console.log(currentUser)
         nav('/user')
        // כאן תוכל להוסיף את הלוגיקה שלך לטיפול בהגשת הטופס
        // לדוג', שליחת המידע לשרת או עדכון המצב באפליקציה
    };

    return (
        <>       <Container component="main" maxWidth="xs">
                 <CssBaseline />
              <Box
                  sx={{
                    marginTop: 8,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                  }}
                >
                  <Avatar sx={{ m: 1, bgcolor: 'lightpink' }}>
                    <LockOutlinedIcon />
                  </Avatar>
                  <Typography component="h1" variant="h5">
                    Sign Up
                  </Typography>
                  <Box component="form" noValidate 
                   sx={{ mt: 3 }}>
                    <Grid container spacing={2}>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <TextField
                                       InputLabelProps={{
                                                             style: { color: 'black' }, // Inline style for label
                                                           }}fullWidth
                                                           autoComplete="given-name"
                                                           name="tz"
                                                           required
                                                           autoFocus
                        id="filled-basic"
                        label="tz:"
                        variant="filled"
                        onChange={(e) => setUser({ tz: e.target.value, name:user.name, password: user.password, telephone:user.telephone ,address:user.address})}
                        {...register("tz", { required: "field is required", pattern: /^\d{9}$/ })}
                    />
                    {errors.tz && <p>The id number is incorrect</p>}
                    {dirtyFields.tz && <p>✔️</p>}
                </div>
                <br />
                <div>
                    <TextField
                        id="filled-basic"
                        label="name:"
                        variant="filled"
                        onChange={(e) => setUser({ tz: user.tz, name:e.target.value, password: user.password, telephone:user.telephone,address:user.address})}
                        {...register("name", { required: "field is required", pattern: /^[A-Za-z]+$/i })}
                    />
                    {errors.name?.type === 'required' && <p>{errors.name.message}</p>}
                    {errors.name?.type === 'pattern' && <p>enter in english</p>}
                    {dirtyFields.name && <p>✔️</p>}
                </div>
                <br />
                <div>
                    <TextField
                        id="filled-basic"
                        label="password:"
                        type="password"
                        variant="filled"
                        onChange={(e) => setUser({ tz:user.tz,name:user.name, password:e.target.value, telephone:user.telephone,address:user.address})}
                        {...register("password",{required: "field is required"})}
                    />
                   {errors.password?.type === 'required' && <p>{errors.password.message}</p>}
                    {dirtyFields.password && <p>✔️</p>}
                </div>
                <br />
                <div>
                    <TextField
                        id="filled-basic"
                        label="telephone:"
                        type="telephone"
                        variant="filled"
                        onChange={(e) => setUser({ tz: user.tz,name:user.name, password: user.password, telephone:e.target.value,address:user.address})}
                        {...register("telephone", {
                            required: 'enter a phone number',
                            pattern: {
                                value: /^[0-9]{10}$/,
                                message: 'the phone number is incorrect',
                            },
                        })}
                    />
                    {errors.telephone && <p>{errors.telephone.message}</p>}
                    {dirtyFields.telephone && <p>✔️</p>}
                </div>
                <br />
                <div>
                    <TextField
                        id="filled-basic"
                        label="address:"
                        variant="filled"
                        onChange={(e) => setUser({ tz: user.tz, name:user.name, password: user.password, telephone:user.telephone,address:e.target.value})}
                        {...register("address", { required: "field is required", pattern: /^[A-Za-z]+$/i })}
                    />
                    {errors.address?.type === 'required' && <p>{errors.address.message}</p>}
                    {errors.name?.type === 'pattern' && <p>enter in english</p>}
                    {dirtyFields.name && <p>✔️</p>}
                </div>
               <br/>
                <input type="submit" value={"send"}  />
            </form>
            </Grid>
          </Box>
         </Box>
       </Container>
        </>
    );
}

