import * as React from 'react';
import { fetchAllOrders } from '../../features/Order/OrderSlice';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { Container, Grid, Card, CardHeader, CardContent, Avatar, Typography } from '@mui/material';
import { pink } from '@mui/material/colors';
import IconButton from '@mui/material/IconButton';
import MoreVertIcon from '@mui/icons-material/MoreVert';

export default function OrderList() {
  const allOrders = useSelector(state => state.orders.arrOrders);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllOrders());
  }, [dispatch]);

  return (
    <Container>
      <Typography variant="h4" align="center" gutterBottom>
        All Orders
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        {allOrders.map(order => (
          <Grid item key={order.id} xs={12} sm={6} md={4}>
            <Card sx={{ bgcolor: pink[50], height: '100%' }}>
              <CardHeader
                avatar={
                  <Avatar sx={{ bgcolor: pink[500] }}>
                    {order.userName[0]}
                  </Avatar>
                }
                action={
                  <IconButton aria-label="settings">
                    <MoreVertIcon />
                  </IconButton>
                }
                title={order.userName}
                subheader={`Total Amount: ₪${order.totalAmount}`}
              />
              <CardContent>
                <Typography variant="body2" color="text.secondary">
                  {order.address}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {order.cart.map(item => (
                    <div key={item.id}>
                      {item.name} - {item.quantity} pcs
                    </div>
                  ))}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
