import { useForm } from "react-hook-form";
import TextField from '@mui/material/TextField';
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from "react-router-dom";
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Password } from '@mui/icons-material';
import { postProduct} from '../../features/Product/ProductSlice';

const AddProduct = () => {
    const [product, setproduct] = useState({ name:" ", description:" " ,price:" ",imgUrl:""});
    const arrP = useSelector(x => x.products.arrProducts);
    const currentP = useSelector(u => u.products.currentproduct);
    const dispatch = useDispatch();
    const nav=useNavigate()
const defaultTheme = createTheme();

    const {
        register,
        handleSubmit,
        getValues,
        formState: { errors, dirtyFields, isValid },
    } = useForm({ mode: 'onBlur' });

    const onSubmit = (data) => {

        dispatch(postProduct({name:data.name, description:data.description, price:data.price,imgUrl:data.imgUrl}))
        console.log(data.name)
         nav('/ProductList')
        // כאן תוכל להוסיף את הלוגיקה שלך לטיפול בהגשת הטופס
        // לדוג', שליחת המידע לשרת או עדכון המצב באפליקציה
    };

    return (
//         <Box
//         sx={{
//           marginTop: 8,
//           display: 'flex',
//           flexDirection: 'column',
//           alignItems: 'center',
//         }}
//       >
//         <Avatar sx={{ m: 1, bgcolor: 'lightpink' }}>
//           {/* <LockOutlinedIcon /> */}
//         </Avatar>
//         <Typography component="h1" variant="h5" >
//           add product
//         </Typography>
// <Box sx={{ maxWidth: 400, margin: 'auto', padding: 3, boxShadow: 3 }}>
// <Typography variant="h5" component="h2" gutterBottom>
// </Typography>         
            <form onSubmit={onSubmit}>

                <>
                    <TextField
                        id="filled-basic"
                        label="name:"
                        variant="outlined"
                        fullWidth
                        margin="normal"                        onChange={(e) => setproduct({ name:e.target.value, description: product.description, price:product.price,imgUrl:product.imgUrl})}
                        {...register("name", { required: "field is required", pattern: /^[A-Za-z]+$/i })}
                    />
                    {errors.name?.type === 'required' && <p>{errors.name.message}</p>}
                    {errors.name?.type === 'pattern' && <p>enter in english</p>}
                    {dirtyFields.name && <p>✔️</p>}
                </>
                <>
                    <TextField
                        id="filled-basic"
                        label="description:"
                        type="description"
                        variant="outlined"
                        fullWidth
                        margin="normal"                        onChange={(e) => setproduct({ name:product.name, description:e.target.value, price:product.price,imgUrl:product.imgUrl})}
                        {...register("description",{required: "field is required"})}
                    />
                   {errors.description?.type === 'required' && <p>{errors.description.message}</p>}
                    {dirtyFields.description && <p>✔️</p>}
                </>
                <>
                    <TextField
                        id="filled-basic"
                        label="price:"
                        type="price"
                        variant="outlined"
                        fullWidth
                        margin="normal"                        
                        onChange={(e) => setproduct({ name:product.name, description: product.description, price:e.target.value,imgUrl:product.imgUrl})}
                        {...register("price", {
                            required: 'enter a price number',
           
                        })}
                    />
                    {errors.price && <p>{errors.price.message}</p>}
                    {dirtyFields.price && <p>✔️</p>}
                </>
                <>
                    <TextField
                              variant="outlined"
                              fullWidth
                              margin="normal"
                        id="filled-basic"
                        label="imgUrl:"
                        onChange={(e) => setproduct({ name:product.name, description: product.description, price:product.price,imgUrl:e.target.value})}
                        {...register("imgUrl", { required: "field is required", pattern: /^[A-Za-z]+$/i })}
                    />
                    {errors.imgUrl?.type === 'required' && <p>{errors.imgUrl.message}</p>}
                    {errors.imgUrl?.type === 'pattern' && <p>enter in english</p>}
                    {dirtyFields.imgUrl && <p>✔️</p>}
                </>
               
                <Button
              color='inherit'
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2,bgcolor:'lightpink' }}
            >
              Sign In
            </Button> 
                       </form>
            // </Box>            </Box>

        
    );
}

export default AddProduct;

