import {fetchAllOrders } from "../../features/Order/OrderSlice";
import Order from './Order';
import Alert from '@mui/material/Alert';
import CircularProgress from '@mui/material/CircularProgress';
import ImageListItem from '@mui/material/ImageListItem';
import Box from '@mui/material/Box';


const  OrderList=()=> {
let arrFromRedux = useSelector(s=>s.orders.arrOrders)
const [filter1, setFilter1] = useState(useSelector(s => s.orders.currentproduct) || []);
const statusFetchData = useSelector(s => s.orders.status)
let name="";

