        <div>
        <input className='nn' type="text" onChange={(e)=>name=e.target.value} placeholder='name'></input>
        {/* <button  onClick={() =>{setFilter1 (arrFromRedux.slice().filter(i => i.name.includes( name)))}}>סינון לפי שם</button>
         <br></br>
          <button onClick={()=>{setFilter1(arrFromRedux.slice().sort((a,b)=>a.price-b.price))}}>סינון לפי מחיר</button>
          <br></br>
          <button onClick={() =>{setFilter1 (arrFromRedux.slice().filter(i => i.name.includes( "")))}}>ביטול סינונים </button>       */}

