import * as React from 'react';
import { fetchAllOrders } from '../../features/Order/OrderSlice';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { Container, Grid, Paper, Typography, Box } from '@mui/material';
import Order from './Order';

export default function OrderList() {
  const userStatus = useSelector((state) => state.users.status);
  const userTzz = useSelector((s) => s.users.currentUser);
  const allOrders = useSelector((state) => state.orders.arrOrders);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllOrders());
  }, [dispatch]);

  const filterArrOrders =
    userStatus === 'customer'
      ? allOrders.filter((order) => order.userName === userTzz.name)
      : allOrders;

  console.log(filterArrOrders);
  console.log(userTzz);

  return (
    <Container
      sx={{
        backgroundColor: '#FCE4EC', // Light pink background
        padding: '20px',
        borderRadius: '8px',
        marginTop: '20px',
      }}
    >
      <Typography
        variant="h4"
        sx={{ color: '#E91E63', textAlign: 'center', marginBottom: '20px' }}
      >
        Orders List
      </Typography>
      <Grid container spacing={2}>
        {filterArrOrders.map((order) => (
          <Grid item key={order.id} xs={12} sm={6} md={4}>
            <Paper
              elevation={3}
              sx={{
                padding: '20px',
                borderRadius: '8px',
                backgroundColor: '#F8BBD0', // Light pink
                border: '1px solid #E91E63', // Pink border
                display: 'flex',
                flexDirection: { xs: 'column', md: 'row' },
                justifyContent: 'space-between',
                alignItems: 'center',
                height: '100%',
              }}
            >
              <Box
                sx={{
                  width: { xs: '100%', md: '50%' },
                  height: { xs: 'auto', md: '100%' },
                  marginBottom: { xs: '16px', md: '0' },
                }}
              >
                <Order order={order} />
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
