import { createSlice, nanoid ,createAsyncThunk } from '@reduxjs/toolkit'
import { getUsers,getUserByIdA,postUserA,loginA } from './UserApi'

const initialState = {
    type:"guest",
    currentUser:null,
    arrUsers:[],
    status: "idle",
    poststatus: "idle"
}

export const fetchAllUsers = createAsyncThunk(
    'user/fetchAllUsers',
    async (thunkAPI) => {
        const res = await getUsers()
        return res
    },
)

export const getUserByPass = createAsyncThunk(
    'user/getUserByPass',
    async (password,thunkAPI) => {
        const res = await getUserByPassA(password)
        return res
    },
)

export const postUser = createAsyncThunk(
    'user/postUser',
    async (newUser,thunkAPI) => {
        const res = await postUserA(newUser)
        return res
    },
)

export const login = createAsyncThunk(
    'user/login',
    async (user,thunkAPI) => {
        const res = await loginA(user)
        return res
    },
)



export const UserSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        logout: (state, action) => {
            state.currentUser = "guest"
            state.status="idle"
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(login.fulfilled, (state, { payload }) => {
                state.currentUser = payload
                state.status = "fullfilled"
                console.log(state.currentUser)
            })
            .addCase(postUser.fulfilled, (state, { payload }) => {
                state.currentUser = payload
                state.status = "fullfilled"
                console.log(state.currentUser)
            })
            .addCase(getUserByPass.fulfilled, (state, { payload }) => {
                state.currentUser = payload
                state.status = "fullfilled"
                console.log(state.currentUser)
            })
            // // You can chain calls, or have separate `builder.addCase()` lines each time
            // .addCase(fetchAllProduct.pending, (state, action) => {
            //     state.status = "pending"
            // })
            // .addCase(fetchAllProduct.rejected, (state, action) => {
            //     state.status = "rejected"
            // })

    },
})


export const {  logout } = UserSlice.actions

export default UserSlice.reducer
