import TextField from '@mui/material/TextField';
import { useDispatch } from 'react-redux';
import { postProduct  } from '../../features/Product/ProductSlice'
import React, { useState } from 'react';
import { useForm } from 'react-hook-form';


import { useNavigate } from "react-router-dom";

const AddProduct = () => {

const {
  register,
  handleSubmit,
  getValues,
  formState: { errors, dirtyFields, isValid },
} = useForm({ mode: 'onBlur' });
const  AddProduct= (prop) => {
    let productToAdd={
        name:" ",
        description:" ",
        price:" ",
        imgUrl:" "
    }
    let dispatch=useDispatch()
    const add=(e)=>{
        e.preventDefault() 
        dispatch(postProduct(productToAdd))
        console.log("add user")

    }
    return (<form onSubmit={handleSubmit(add)}>
       <TextField id="filled-basic" label="name" variant="filled" onChange={(e)=>productToAdd.name=e.target.value} />
       <TextField id="filled-basic" label="description" variant="filled" onChange={(e)=>productToAdd.description=e.target.value}/>
       <TextField id="filled-basic" label="price" variant="filled" onChange={(e)=>productToAdd.price=e.target.value}/>
       <TextField id="filled-basic" label="imgUrl" variant="filled" onChange={(e)=>productToAdd.imgUrl=e.target.value}/>
       <input type="submit" value="send"></input>
       </form> );
}}
 
export default  AddProduct;
