import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import ImageListItemBar from '@mui/material/ImageListItemBar';

import { addToShoppingCart } from '../../features/Order/OrderSlice';

export default function Product(props) {
    let p = props.myProduct;
    const userStatus = useSelector(store => store.users.status);
    const [quantity, setQuantity] = useState(1);
    const [showCart, setShowCart] = useState(false);
    const [bool, setBool] = useState(1);
    const dispatch = useDispatch();
    let nav = useNavigate();

    const addToCart = () => {
        setBool(1);
        dispatch(addToShoppingCart({ p, quantity }));
        setShowCart(true);
        setTimeout(() => {
            setShowCart(false);
        }, 5000);
    };

    useEffect(() => {
        if (showCart) {
            const timer = setTimeout(() => {
                setShowCart(false);
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [showCart]);

    const handleMoreDetails = () => {
        nav('/productToAdd', { state: { p } });
    };

    const handleEdit = () => {
        // פונקציה לעריכת המוצר
        nav('addProduct/' + p.id)    };

    return (
        <>
            <Card sx={{ maxWidth: 300, m: 3 }}>
                <CardHeader title={p.description} />
                {<CardMedia component="img" height="140" image={p.imgUrl} />}
                <CardContent>
                    {userStatus === 'admin' ? (
                        <button onClick={handleEdit}>Edit</button>
                    ) : (
                        <button onClick={handleMoreDetails}>More Details</button>
                    )}
                </CardContent>
            </Card>
            <ImageListItemBar
                sx={{
                    border: '2px solid pink', // Add a pink border
                    borderTop: 0
                }}
                position="below"
                title={p.name}
                subtitle={"₪" + p.price}
                actionIcon={
                    <IconButton
                        sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                        aria-label={`cart `}
                    >
                        {bool === 1 && (
                            <IconButton>
                                <AddShoppingCartIcon
                                    onClick={() => setBool(0)}
                                />
                            </IconButton>
                        )}
                        {bool === 0 && (
                            <IconButton>
                                <div className="quantity-container">
                                    <div className="quantity-controls">
                                        <button
                                            className="quantity-button"
                                            onClick={() => setQuantity(prevQuantity => prevQuantity - 1)}
                                            disabled={quantity === 1}
                                        >
                                            <span>-</span>
                                        </button>
                                        <span>{quantity}</span>
                                        <button
                                            className="quantity-button"
                                            onClick={() => setQuantity(prevQuantity => prevQuantity + 1)}
                                        >
                                            <span>+</span>
                                        </button>
                                    </div>
                                    <button
                                        className="add-to-cart-button"
                                        onClick={addToCart}
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </IconButton>
                        )}
                    </IconButton>
                }
            />
        </>
    );
}
