import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { login } from '../../features/User/UserSlice'
import { useSelector, useDispatch, Provider } from 'react-redux'
import { useNavigate } from "react-router-dom";
import { colors } from '@mui/material';
import { lightGreen, pink } from '@mui/material/colors';


  // TODO remove, this demo shouldn't need to reset the theme.
  
  const defaultTheme = createTheme();
export default function LogIn() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [user, setUser] = useState({ email: "", password: "" });
    let myUser=useSelector(s=>s.users.currentUser)
    const dis = useDispatch();
    let nav = useNavigate()

    const onSubmit = (event) => {
        // event.preventDefault();
        // const data = new FormData(event.currentTarget);
        dis(login({ fullName: event.fullName, password: event.passwordd }))
        if(myUser!=null)
        nav('/usernav');
      else
      nav('guest')
      };
      return (<>
       <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'lightpink' }}>
            {/* <LockOutlinedIcon /> */}
          </Avatar>
          <Typography component="h1" variant="h5" >
            Log In
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              bgcolor="lightpink"
              margin="normal"
              required
              fullWidth
              id="fullName"
              label=" fullName"
              name="fullName"
              autoComplete="fullName"
              onChange={(e) => setUser({ fullName: e.target.value, password: user.password })}

              autoFocus
              InputLabelProps={{
                style: { color: 'black' }, // Inline style for label
              }}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={(e) => setUser({ fullName: user.fullName, password: e.target.value })}
              InputLabelProps={{
                style: { color: 'black' }, // Inline style for label
              }}
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="default" />}
              label="Remember me"
            />
            <Button
              color='inherit'
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2,bgcolor:'lightpink' }}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item  >
                <Link href="\signIn" style={{ color: 'pink' ,textDecoration:'none'}}                >
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  
 </> )
}
